mapper.update:
update SGAI_SU_USER set VERSION=VERSION+1 

  ,PIN = #{pin,jdbcType=VARCHAR}


  ,USER_NAME_CN = #{userNameCn,jdbcType=VARCHAR}


  ,USER_NAME_EN = #{userNameEn,jdbcType=VARCHAR}


  ,USER_NAME_VN = #{userNameVn,jdbcType=VARCHAR}


  ,USER_STATE = #{userState,jdbcType=DECIMAL}


  ,COUNTRY = #{country,jdbcType=DECIMAL}


  ,SEX = #{sex,jdbcType=DECIMAL}


  ,PASSWORD = #{password,jdbcType=VARCHAR}


  ,EMAIL = #{email,jdbcType=VARCHAR}


  ,EXT_TEL = #{extTel,jdbcType=VARCHAR}


  ,PHONE1 = #{phone1,jdbcType=VARCHAR}


  ,PHONE2 = #{phone2,jdbcType=VARCHAR}


  ,GROUP_ID = #{groupId,jdbcType=DECIMAL}


  ,SHIFT_ID = #{shiftId,jdbcType=DECIMAL}


  ,PWD_MODIFY_DATE = #{pwdModifyDate,jdbcType=TIMESTAMP}


  ,UPDATED_BY = #{updatedBy,jdbcType=VARCHAR}


  ,UPDATED_TIMESTAMP = #{updatedTimestamp,jdbcType=TIMESTAMP}


  ,SHORT_NAME = #{shortName,jdbcType=VARCHAR}


  ,BRANCH_COMPANY_ID = #{branchCompanyId,jdbcType=VARCHAR}


  ,WORKSHOP_ID = #{workshopId,jdbcType=VARCHAR}


  ,DEPT_CODE = #{deptCode,jdbcType=VARCHAR}


  ,STAFF_SID = #{staffSid,jdbcType=DECIMAL}


  ,ORG_SID = #{orgSid,jdbcType=DECIMAL}

where SID = #{sid,jdbcType=DECIMAL}  and VERSION = #{version,jdbcType=DECIMAL}

mapper.select:
select 

from SGAI_SU_USER

  
     AND SID = #{sid,jdbcType=DECIMAL}
  
  
     AND PIN = #{pin,jdbcType=VARCHAR}
  
  
     AND USER_NAME_CN = #{userNameCn,jdbcType=VARCHAR}
  
  
     AND USER_NAME_EN = #{userNameEn,jdbcType=VARCHAR}
  
  
     AND USER_NAME_VN = #{userNameVn,jdbcType=VARCHAR}
  
  
     AND USER_STATE = #{userState,jdbcType=DECIMAL}
  
  
     AND COUNTRY = #{country,jdbcType=DECIMAL}
  
  
     AND SEX = #{sex,jdbcType=DECIMAL}
  
  
     AND PASSWORD = #{password,jdbcType=VARCHAR}
  
  
     AND EMAIL = #{email,jdbcType=VARCHAR}
  
  
     AND EXT_TEL = #{extTel,jdbcType=VARCHAR}
  
  
     AND PHONE1 = #{phone1,jdbcType=VARCHAR}
  
  
     AND PHONE2 = #{phone2,jdbcType=VARCHAR}
  
  
     AND GROUP_ID = #{groupId,jdbcType=DECIMAL}
  
  
     AND SHIFT_ID = #{shiftId,jdbcType=DECIMAL}
  
  
     AND PWD_MODIFY_DATE = #{pwdModifyDate,jdbcType=TIMESTAMP}
  
  
     AND CREATED_BY = #{createdBy,jdbcType=VARCHAR}
  
  
     AND CREATED_TIMESTAMP = #{createdTimestamp,jdbcType=TIMESTAMP}
  
  
     AND UPDATED_BY = #{updatedBy,jdbcType=VARCHAR}
  
  
     AND UPDATED_TIMESTAMP = #{updatedTimestamp,jdbcType=TIMESTAMP}
  
  
     AND VERSION = #{version,jdbcType=DECIMAL}
  
  
     AND SHORT_NAME = #{shortName,jdbcType=VARCHAR}
  
  
     AND BRANCH_COMPANY_ID = #{branchCompanyId,jdbcType=VARCHAR}
  
  
     AND WORKSHOP_ID = #{workshopId,jdbcType=VARCHAR}
  
  
     AND DEPT_CODE = #{deptCode,jdbcType=VARCHAR}
  
  
     AND STAFF_SID = #{staffSid,jdbcType=DECIMAL}
  
  
     AND ORG_SID = #{orgSid,jdbcType=DECIMAL}
  


   ORDER BY ${order}


   ORDER BY SID ASC
